<script setup>
import { reactive } from 'vue';
// const nome = "joao gustavo"
// const meuObj = {
//     nome: "joao",
//     filmeFavorito: "shrek",

// }    

function dizOla(nome) {
    return`${nome} diz oi`;
}

const enderecoImagem = "data:image/jpeg;base64,";
const rodrigoGarro = "data:image/jpeg;base64,";

const botaoHabilitado = true
 
const gostaDoMemphis = false
const gostaDoGarro = false

const autorizacao = false

// let contador = 0
const estado = reactive({
    contador: 0,
    email: '',
})

function incrementar() {
    estado.contador++;
}

function decrementar() {
    estado.contador--;
}

function alteraEmail(evento) {
   estado.email = evento.target.value;
}

</script>

<template>
<h1>{{ dizOla("guilherme") }}</h1>
<img v-if="gostaDoMemphis" v-bind:src="enderecoImagem" alt="">
<img v-else-if="gostaDoGarro" :src="rodrigoGarro" alt="">
<h2 v-else>Não torce pro Corinthians</h2>

<h1 v-if="autorizacao">Bem Vindo</h1>
<h1 v-else>Não possui acesso</h1>

<button :disabled="!botaoHabilitado">enviar mensagem</button>

<br />
<hr />

{{ estado.contador }}

<button @click="incrementar" type="button">+</button>
<button @click="decrementar" type="button">-</button>

<br />
<hr />

{{ estado.email }}
<input type="email" @keyup="alteraEmail">

</template>

